INSERT INTO `wp_comments` (`comment_ID`, `comment_post_ID`, `comment_author`, `comment_author_email`, `comment_author_url`, `comment_author_IP`, `comment_date`, `comment_date_gmt`, `comment_content`, `comment_karma`, `comment_approved`, `comment_agent`, `comment_type`, `comment_parent`, `user_id`) VALUES
(1, 1, 'Un comentarista de WordPress', 'wapuu@wordpress.example', 'https://wordpress.org/', '', '2018-10-08 13:45:46', '2018-10-08 13:45:46', 'Hola, esto es un comentario.\nPara empezar a moderar, editar y borrar comentarios, por favor, visita la pantalla de comentarios en el escritorio.\nLos avatares de los comentaristas provienen de <a href=\"https://gravatar.com\">Gravatar</a>.', 0, 'post-trashed', '', '', 0, 0),
(2, 34, 'WooCommerce', '', '', '', '2018-10-08 23:22:04', '2018-10-08 23:22:04', 'El estado del pedido cambió de Pendiente de pago a Fallido.', 0, '1', 'WooCommerce', 'order_note', 0, 0),
(3, 34, 'WooCommerce', '', '', '', '2018-10-08 23:22:32', '2018-10-08 23:22:32', 'Pendiente de pago por transferencia El estado del pedido cambió de Fallido a En espera.', 0, '1', 'WooCommerce', 'order_note', 0, 0),
(4, 35, 'WooCommerce', '', '', '', '2018-10-08 23:26:59', '2018-10-08 23:26:59', 'El estado del pedido cambió de Pendiente de pago a Fallido.', 0, '1', 'WooCommerce', 'order_note', 0, 0),
(5, 36, 'WooCommerce', '', '', '', '2018-10-09 02:16:48', '2018-10-09 02:16:48', 'El pedido sin pagar ha sido cancelado - se ha alcanzado el limite de tiempo. El estado del pedido cambió de Pendiente de pago a Cancelado.', 0, '1', 'WooCommerce', 'order_note', 0, 0);
